import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

import { useState } from "react";

function Questions({ q, a }) {
  const [show, setShow] = new useState(true);
  const [sign, setSign] = new useState("+");

  function toggle() {
    if (show) {
      setSign("-");
      setShow(false);
    } else {
      setSign("+");
      setShow(true);
    }
  }

  return (
    <>
      <div className="qna ">
        <div className="que border-5  bg-danger ">
          <button onClick={toggle}> {sign} </button>
          <h3>{q}</h3>
        </div>
        {show && <p className="ans">{a}</p>}
      </div>
    </>
  );
}

export default Questions;
